// Example: the correct melody as a sequence of note IDs
const correctMelody = ['C', 'E', 'G', 'C'];
let playerInputMelody = [];

function onNoteHit(noteId) {
    playerInputMelody.push(noteId);
    // Check progressively
    for (let i = 0; i < playerInputMelody.length; i++) {
        if (playerInputMelody[i] !== correctMelody[i]) {
            // Wrong note - reset puzzle
            playerInputMelody = [];
            playErrorSound();
            return;
        }
    }
    // If length matches and all correct, puzzle solved
    if (playerInputMelody.length === correctMelody.length) {
        openSecretDoor();
    }
}

