// Example: 3 pressure plates must be activated in the correct order within time
const correctOrder = ['plate1','plate2','plate3'];
let currentStep = 0;

function onPlateStepped(plate) {
    if (plate.name === correctOrder[currentStep]) {
        currentStep++;
        plate.activate(); // visually indicate
        if (currentStep === correctOrder.length) {
            puzzleComplete();
        }
    } else {
        // wrong plate stepped, reset puzzle
        currentStep = 0;
        resetAllPlates();
    }
    // Optionally, set a timer to reset if too slow
    if (currentStep > 0) {
        this.time.delayedCall(5000, () => { currentStep = 0; resetAllPlates(); });
    }
}

