    itemSprite.destroy();
});

function addToInventory(item) {
    // item could have properties like item.name, item.quantity
    player.inventory.push(item);
    updateInventoryUI();
}

// Using an item (e.g., health potion)
function useItem(itemName) {
    let index = player.inventory.findIndex(it => it.name === itemName);
    if (index !== -1) {
        let item = player.inventory[index];
        if (item.name === 'Health Potion') {
            player.health = Math.min(player.maxHealth, player.health + 50);
        }
        item.quantity -= 1;
        if (item.quantity <= 0) {
            player.inventory.splice(index, 1);
        }
        updateInventoryUI();
    }
}

