// Example pseudo-recipe
const recipes = {
    'health_potion': { herb: 2, water: 1 }
};

function craft(itemName) {
    let recipe = recipes[itemName];
    if (recipe && hasMaterials(recipe)) {
        // remove materials from inventory
        for (let material in recipe) {
            player.inventory[material] -= recipe[material];
        }
        // add crafted item
        player.inventory[itemName] = (player.inventory[itemName] || 0) + 1;
        playSound('craftSuccess');
    } else {
        playSound('craftFail');
    }
}

function hasMaterials(recipe) {
    for (let material in recipe) {
        if (!player.inventory[material] || player.inventory[material] < recipe[material]) {
            return false;
        }
    }
    return true;
}

// Equipping an item (e.g., a new weapon)
function equipItem(item) {
    if (item.type === 'weapon') {
        player.equippedWeapon = item;
        player.attackDamage = item.attackValue;
        player.attackType = item.attackType;
    } else if (item.type === 'armor') {
        player.equippedArmor = item;
        player.defense = item.defenseValue;
    }
    updateEquipmentUI();
}

