    callback: () => {
        player.hunger = Math.max(0, player.hunger - 1);
        player.thirst = Math.max(0, player.thirst - 1);
        if (player.hunger === 0 || player.thirst === 0) {
            player.health -= 1; // lose health if starving or dehydrated
        }
    }
});

// Using food item to restore hunger
function eatFood(item) {
    player.hunger = Math.min(100, player.hunger + item.foodValue);
    removeItemFromInventory(item);
}

