// Assuming use of Arcade physics for simplicity
if (grappleButton.isDown && !player.isGrappling) {
    let hookTarget = getGrappleTarget(player.x, player.y, aimAngle);
    if (hookTarget) {
        player.isGrappling = true;
        player.body.allowGravity = false;
        // Calculate direction vector toward target
        let dx = hookTarget.x - player.x;
        let dy = hookTarget.y - player.y;
        let dist = Math.sqrt(dx*dx + dy*dy);
        player.setVelocity(dx/dist * grappleSpeed, dy/dist * grappleSpeed);
        // Optionally create a rope line graphic
        ropeLine = this.add.line(0, 0, player.x, player.y, hookTarget.x, hookTarget.y, 0xFFFFFF);
    }
}
// Update rope line each frame if grappling
if (player.isGrappling) {
    ropeLine.setTo(player.x, player.y, hookTarget.x, hookTarget.y);
    // If player reached near the hook or presses jump to cancel
    if (Phaser.Math.Distance.Between(player.x, player.y, hookTarget.x, hookTarget.y) < 10 || cancelKey.isDown) {
        player.isGrappling = false;
        player.body.allowGravity = true;
        ropeLine.destroy();
    }
}

