this.physics.add.overlap(player, ropeEnd, () => {
    player.onRope = true;
    player.body.allowGravity = false;
    player.ropeAnchor = {x: ropeAnchor.x, y: ropeAnchor.y};
    player.swingAngle = Math.PI / 4; // starting angle
    player.swingSpeed = 0; // no initial angular velocity
});
// In update, if on rope, simulate swing
if (player.onRope) {
    // basic pendulum physics
    let length = Phaser.Math.Distance.Between(player.ropeAnchor.x, player.ropeAnchor.y, player.x, player.y);
    let gravityForce = 0.98; // some gravity constant for swing
    player.swingSpeed += (-gravityForce / length) * Math.sin(player.swingAngle);
    player.swingAngle += player.swingSpeed;
    // Update player position based on angle
    player.x = player.ropeAnchor.x + length * Math.sin(player.swingAngle);
    player.y = player.ropeAnchor.y + length * Math.cos(player.swingAngle);
    // If jump key pressed to release
    if (cursors.up.isDown) {
        player.onRope = false;
        player.body.allowGravity = true;
        // Apply momentum from swing to player velocity
        player.setVelocity(player.x - player.ropeAnchor.x, player.y - player.ropeAnchor.y);
    }
}

// Example: bullet hitting a destructible block
this.physics.add.collider(bullet, destructibleBlock, () => {
    destructibleBlock.play('break');
    destructibleBlock.on('animationcomplete', () => {
        destructibleBlock.destroy();
    });
    bullet.destroy();
});
// Example: bomb explosion affecting tiles
bomb.explode = function() {
    let explosionRadius = 2; // in tiles
    let tileX = layer.worldToTileX(bomb.x);
    let tileY = layer.worldToTileY(bomb.y);
    for (let dx = -explosionRadius; dx <= explosionRadius; dx++) {
        for (let dy = -explosionRadius; dy <= explosionRadius; dy++) {
            let tile = layer.getTileAt(tileX+dx, tileY+dy);
            if (tile && tile.properties.destructible) {
                layer.removeTileAt(tileX+dx, tileY+dy);
            }
        }
    }
};

// Slow motion example
if (slowMoKey.isDown && !this.slowMoActive) {
    this.slowMoActive = true;
    this.physics.world.timeScale = 0.2;
    this.time.delayedCall(1000, () => {
        // restore normal speed after 1 second real-time (5 seconds game-time if timeScale=0.2)
        this.physics.world.timeScale = 1;
        this.slowMoActive = false;
    });
}
// Time rewind example (player only for simplicity)
stateBuffer.push({x: player.x, y: player.y, vx: player.body.velocity.x, vy: player.body.velocity.y});
if (rewindKey.isDown) {
    if (stateBuffer.length > 0) {
        let prevState = stateBuffer.pop();
        player.x = prevState.x;
        player.y = prevState.y;
        player.body.velocity.x = prevState.vx;
        player.body.velocity.y = prevState.vy;
    }
}
// (In a full implementation, you'd loop this rewind over a short duration and handle other objects similarly.)

// Magnetic boots example: stick to a ceiling
if (player.magnetActive && player.isNearMetalCeiling) {
    player.body.allowGravity = false;
    // pull upward
    player.setVelocityY(-50);
    // if reached ceiling
    if (player.y <= ceilingY + player.height/2) {
        player.y = ceilingY + player.height/2;
        player.body.velocity.y = 0;
    }
} else {
    player.body.allowGravity = true;
}
// Magnet gun example: pulling metal objects
metalGroup.getChildren().forEach(metal => {
    if (player.magnetActive) {
        let dx = player.x - metal.x;
        let dy = player.y - metal.y;
        let dist = Math.sqrt(dx*dx + dy*dy);
        if (dist < magnetRange) {
            metal.body.velocity.x += (dx/dist) * magnetForce;
            metal.body.velocity.y += (dy/dist) * magnetForce;
        }
    }
});

// Wind zone area effect
if (windZone.getBounds().contains(player.x, player.y)) {
    // Apply wind push to the right
    player.setAccelerationX(200);
} else {
    player.setAccelerationX(0);
}
// Ice surface effect
if (player.onIce) {
    player.body.drag.x = 10; // very low drag, so player slides
    // maybe limit max velocity on ice if needed
} else {
    player.body.drag.x = defaultDrag;
}
// Water region effect
if (player.inWater) {
    player.setGravityY(300); // less than normal gravity
    if (cursors.up.isDown) {
        // simple swimming upward force
        player.setVelocityY(-100);
    }
    // water resistance
    player.body.drag.x = 100;
} else {
    player.setGravityY(normalGravity);
    player.body.drag.x = defaultDrag;
}

// Portal teleportation
this.physics.add.overlap(player, portals, (player, portal) => {
    if (!player.justTeleported) {
        let destination = portals.find(p => p.id === portal.targetId);
        if (destination) {
            player.setPosition(destination.x, destination.y);
            player.justTeleported = true;
            // Prevent immediate re-trigger
            this.time.delayedCall(500, () => { player.justTeleported = false; });
        }
    }
});
// Blink ability
if (blinkKey.isDown && player.canBlink) {
    let targetX = player.x + blinkDistance * (player.facingRight ? 1 : -1);
    // Check if target is free (no wall)
    if (!tileLayer.hasTileAtWorldXY(targetX, player.y)) {
        // Teleport player
        let oldX = player.x, oldY = player.y;
        player.setPosition(targetX, player.y);
        // Play teleport effects
        playTeleportEffect(oldX, oldY);
        playTeleportEffect(player.x, player.y);
        player.canBlink = false;
        this.time.delayedCall(1000, () => { player.canBlink = true; });
    }
}

// Light receptor puzzle
if (lightSource.on && hasLineOfSight(lightSource, lightReceptor)) {
    lightReceptor.activated = true;
    door.open();
} else {
    lightReceptor.activated = false;
    door.close();
}
// Toggling hidden platforms with global light switch
if (lightSwitch.isOn) {
    hiddenPlatforms.forEach(platform => {
        platform.disableBody(true, true); // hide and disable collision
    });
    visibleInLightPlatforms.forEach(platform => {
        platform.enableBody(false, 0, 0, true, true);
    });
} else {
    hiddenPlatforms.forEach(platform => {
        platform.enableBody(false, 0, 0, true, true);
    });
    visibleInLightPlatforms.forEach(platform => {
        platform.disableBody(true, true);
    });
}
// Stealth integration: if implementing shadows for hiding
if (playerInShadowArea) {
    player.isHidden = true;
}
if (playerExposedToLight) {
    player.isHidden = false;
}
